### call bruteLog with p = PARAM_P and g = PARAM_G

def bruteLog(g, c, p):
    s = 1
    for i in range(p):
        if s == c:
            return i
        s = (s * g) % p
        if s == c:
            return i + 1
    return -1

def EG_generate_nonce():
    return randint(1, PARAM_P - 2)

def EG_generate_keys():
    private_key = EG_generate_nonce()
    public_key = pow(PARAM_G, private_key, PARAM_P)
    return (private_key, public_key)

## multiplicative version
def EGM_encrypt(m, public_key):
    r = EG_generate_nonce()
    c1 = pow(PARAM_G, r, PARAM_P)
    c2 = (m * pow(public_key, r, PARAM_P)) % PARAM_P
    return (c1, c2)

## additive version
def EGA_encrypt(m, public_key):
    r = EG_generate_nonce()
    c1 = pow(PARAM_G, r, PARAM_P)
    c2 = (pow(PARAM_G, m, PARAM_P) * pow(public_key, r, PARAM_P)) % PARAM_P
    return (c1, c2)


def EG_decrypt(private_key, c1, c2):
    tmp = pow(c1, private_key, PARAM_P)
    m = (c2 * mod_inv(tmp, PARAM_P)) % PARAM_P
    return m
